import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs/internal/BehaviorSubject';

@Injectable({
  providedIn: 'root'
})
export class CommunicationServiceService {
  public bsTime = new BehaviorSubject({ h: 12, m: 12, s: 12, keepRunning: true })
  public bsToggle = new BehaviorSubject({ isRunning: true })
  public isRunning = true

  constructor() { }

  relayTime(hour: number, minute: number, second: number) {
    this.bsTime.next({ h: hour, m: minute, s: second, keepRunning: true })
  }

  toggleTime() {
    this.bsTime.next({h:0, m:0, s:0, keepRunning:!this.isRunning})
    this.isRunning = !this.isRunning
  }
}
